rows = rawInput.splitlines()
width = len(rows[0])
height = len(rows)

rowsToExpand = []
columnsToExpand = []
for i in range(height):
    if '#' not in rows[i]:
        rowsToExpand.append(i)

for j in range(width):
    found = False
    for i in range(height):
        if rows[i][j] == '#':
            found = True
            break

    if not found:
        columnsToExpand.append(j)

print(rowsToExpand, columnsToExpand)

nodes = []

for i in range(height):
    for j in range(width):
        if rows[i][j] == '#':
            nodes.append((i, j))

totalDist = 0
expansionFactor = 1000000
for n1 in nodes:
    for n2 in nodes:
        if n1 >= n2:
            continue
        dist = 0
        maxI = max(n1[0], n2[0])
        minI = min(n1[0], n2[0])
        maxJ = max(n1[1], n2[1])
        minJ = min(n1[1], n2[1])
        for i in range(minI, maxI):
            if i in rowsToExpand:
                dist += expansionFactor
            else:
                dist += 1
        for j in range(minJ, maxJ):
            if j in columnsToExpand:
                dist += expansionFactor
            else:
                dist += 1

        print(n1, n2, dist)
        totalDist += dist

print(totalDist)